import React, { Component } from 'react';

import { Row, Col, Button } from 'react-bootstrap';

class Control extends Component {
    state = {
        pump: false,
        irrigation: false,
        lights: false,
        reset: false
    };

    render() {
        const styles = {
            base: {
                height: '150px',
                boxShadow: '0 3px 10px 0 rgba(0, 0, 0, 0.05)'
            },
            bb: {
                borderBottom: '1px solid rgba(0, 0, 0, 0.05)',
            },
            br: {
                borderRight: '1px solid rgba(0, 0, 0, 0.05)',
            }
        };

        const controlList = [
            {
                context: 'pump',
                style: { ...styles.bb, ...styles.br, ...styles.base }
            },
            {
                context: 'irrigation',
                style: { ...styles.bb, ...styles.base }
            },
            {
                context: 'lights',
                style: { ...styles.br, ...styles.base }
            },
            {
                context: 'reset',
                style: { ...styles.base }
            }
        ];

        const onPress = context => {
            this.setState({
                [context]: !this.state[context]
            });
        };
        console.log('chekcing state', this.state)
        return (
            <Col xl={{ span: 6 }} className='p-3 mt-3' >
                <Row>
                    {(controlList || []).map(control => (
                        <Col xs={{ span: 6 }} className='p-0'>
                            <div style={control.style}>
                                <p className='p-3'>{control.context}: on</p>
                                <div className='text-center'>
                                    <Button
                                        variant={this.state[control.context] ? 'danger' : 'primary'}
                                        onClick={() => onPress(control.context)}
                                        size='lg'
                                    > {control.context}
                                    </Button>
                                </div>
                            </div>
                        </Col>
                    ))}
                </Row>
            </Col>
        )
    }
}

export default Control;
