import React, { Component } from 'react';

import { Row, Col, Button } from 'react-bootstrap';

class Control extends Component {
    state = {

    };

    render() {
        const Content = props => {
            let context = props.context;
            context = context.charAt(0).toUpperCase() + context.slice(1);
            return (
                <Col xs={{ span: 6 }} className='p-0'>
                    <div style={props.styles}>
                        <div className='p-3'>
                            <span>{context}: on</span>
                        </div>
                        <div className='text-center'>
                            <Button variant='primary' size='lg'>{context}</Button>
                        </div>
                    </div>
                </Col>
            )
        };

        return (
            <Col xl={{ span: 6 }} className='p-3 mt-3'>
                <Row>
                    <Content styles={{ ...styles.bb, ...styles.br, ...styles.base }} context='pump' />
                    <Content styles={{ ...styles.bb, ...styles.base }} context='irrigation' />
                    <Content styles={{ ...styles.br, ...styles.base }} context='lights' />
                    <Content styles={{ ...styles.base }} context='reset' />
                </Row>
            </Col >
        )
    }
}

const styles = {
    base: {
        height: '150px',
        boxShadow: '0 3px 10px 0 rgba(0, 0, 0, 0.05)'
    },
    bb: {
        borderBottom: '1px solid rgba(0, 0, 0, 0.05)',
    },
    br: {
        borderRight: '1px solid rgba(0, 0, 0, 0.05)',
    }
}

export default Control;
